import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "./HowTo.css";

const HowTo = () => {
  const [visibleSteps, setVisibleSteps] = useState([]);

  useEffect(() => {
    const revealSteps = () => {
      steps.forEach((_, index) => {
        setTimeout(() => {
          setVisibleSteps((prev) => [...prev, index]);
        }, index * 400); // D√©calage progressif entre chaque √©tape
      });
    };

    revealSteps();
  }, []);

  const steps = [
    {
      number: "1",
      title: "Inscrivez-vous",
      description:
        "Cr√©ez votre compte en quelques clics pour acc√©der aux opportunit√©s.",
    },
    {
      number: "2",
      title: "Acc√©dez aux offres",
      description: "Consultez les offres disponibles et postulez en un clic.",
    },
    {
      number: "3",
      title: "Partagez une offre",
      description:
        "Aidez d‚Äôautres √©tudiants en partageant les opportunit√©s que vous lib√©rez.",
    },
  ];

  return (
    <div className="howto-container">
      <h1 className="howto-title">
        Pass'Alternance en 3 √©tapes simples et rapides !
      </h1>
      <h2 className="howto-subtitle">
        Trouvez une alternance rapidement et partagez les offres lib√©r√©es ! üöÄ
      </h2>

      <div className="howto-steps">
        {steps.map((step, index) => (
          <motion.div
            key={step.number}
            className={`howto-step ${
              visibleSteps.includes(index) ? "visible" : ""
            }`}
            initial={{ opacity: 0, y: 30 }}
            animate={{
              opacity: visibleSteps.includes(index) ? 1 : 0,
              y: visibleSteps.includes(index) ? 0 : 30,
            }}
            transition={{ duration: 0.8, delay: index * 0.3 }}
          >
            <div className="howto-step-number">{step.number}</div>
            <div className="howto-step-content">
              <h3 className="howto-step-title">{step.title}</h3>
              <p className="howto-step-description">{step.description}</p>
            </div>
          </motion.div>
        ))}
      </div>

      <div className="howto-buttons">
        <button className="howto-button">Je cherche une alternance</button>
        <button className="howto-button">Je publie une offre</button>
      </div>
    </div>
  );
};

export default HowTo;
