import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import "./Filters.css";

const Filters = ({ onFiltersChange }) => {
  const [search, setSearch] = useState("");
  const [minSalary, setMinSalary] = useState(0);
  const [type, setType] = useState("");

  // Mettre à jour les filtres uniquement quand un état change
  useEffect(() => {
    onFiltersChange({ search, minSalary: parseInt(minSalary, 10), type });
  }, [search, minSalary, type, onFiltersChange]);

  return (
    <motion.div
      className="filters"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8, ease: "easeOut" }}
    >
      {/* Champ de recherche */}
      <label>
        Recherche :
        <input
          type="text"
          placeholder="Rechercher un poste"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </label>

      {/* Slider pour le salaire */}
      <label>
        Salaire minimum (€) :
        <input
          type="range"
          min="0"
          max="5000"
          step="100"
          value={minSalary}
          onChange={(e) => setMinSalary(e.target.value)}
        />
        <span>{minSalary} €</span>
      </label>

      {/* Sélection du type d'alternance */}
      <label>
        Type d'alternance :
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="">Tous les types</option>
          <option value="stage">Stage</option>
          <option value="apprentissage">Apprentissage</option>
        </select>
      </label>

      {/* Bouton de réinitialisation */}
      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={() => {
          setSearch("");
          setMinSalary(0);
          setType("");
          onFiltersChange({ search: "", minSalary: 0, type: "" });
        }}
        className="reset-button"
      >
        Réinitialiser
      </motion.button>
    </motion.div>
  );
};

export default Filters;
