const createStars = () => {
  const stars = [];
  for (let i = 0; i < 50; i++) {
    const size = Math.random() * 6;
    const left = Math.random() * 100;
    const duration = Math.random() * 5 + 5;

    stars.push(
      <div
        key={i}
        className="star"
        style={{
          left: `${left}%`,
          width: `${size}px`,
          height: `${size}px`,
          animationDuration: `${duration}s`,
          top: `${Math.random() * 100}vh`,
        }}
      ></div>
    );
  }
  return stars;
};

const createBubbles = () => {
  const bubbles = [];
  for (let i = 0; i < 20; i++) {
    const size = Math.random() * 20 + 10;
    const left = Math.random() * 100;
    const duration = Math.random() * 5 + 5;

    bubbles.push(
      <div
        key={i}
        className="bubble"
        style={{
          left: `${left}%`,
          width: `${size}px`,
          height: `${size}px`,
          animationDuration: `${duration}s`,
          bottom: `-${size}px`,
        }}
      ></div>
    );
  }
  return bubbles;
};
