import React from "react";
import { motion } from "framer-motion";
import "./HowTo.css";

const HowTo = () => {
  return (
    <div className="howto-container">
      <h1 className="howto-title">Comment fonctionne Pass'Alternance en 3 Ã©tapes simples et rapides ?</h1>
      <h2 className="howto-subtitle">5 Things to Practice at Home</h2>
      <div className="howto-steps">
        <div className="steps">
          {[
            { number: "1", title: "Teaching Methods" },
            { number: "2", title: "Effective Education" },
            { number: "3", title: "Online Learning" },
          ].map((step, index) => (
            <motion.div
              key={step.number}
              className="howto-step"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true, amount: 0.3 }}
              transition={{ duration: 1, delay: index * 0.3, ease: "easeOut" }}
            >
              <div className="howto-step-number">{step.number}</div>
              <div className="howto-step-content">
                <h3 className="howto-step-title">{step.title}</h3>
                <p className="howto-step-description">
                  Sample text. Click to select the text box. Click again or
                  double click to start editing the text.
                </p>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
      <div className="howto-buttons">
        <button className="howto-button">Je cherche une alternance</button>
        <button className="howto-button">Je publie une offre</button>{" "}
      </div>
    </div>
  );
};

export default HowTo;
