import React, { useState, useEffect } from "react";
import "./Offers.css";


const Offers = ({ offers, setOffers }) => {
  const [selectedOffer, setSelectedOffer] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    company: "",
    location: "",
    salary: "",
    description: "",
    lien: "",
  });


  // üõ† Charger et trier les offres
  useEffect(() => {
    fetchOffers();
  }, []);


  const fetchOffers = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/offers");
      if (!response.ok) throw new Error("Erreur lors du chargement des offres.");
      const data = await response.json();


      // üìÖ Trier les offres du plus r√©cent au plus ancien
      const sortedOffers = data.sort((a, b) => new Date(b.created_at) - new Date(a.created_at));
      setOffers(sortedOffers);
    } catch (err) {
      console.error("Erreur :", err.message);
    }
  };


  // üõ† Fermer la popup
  const closePopup = () => {
    setSelectedOffer(null);
    setEditMode(false);};


    // üõ† Modifier une offre
    const handleUpdateOffer = async () => {
      const token = localStorage.getItem("token");
  
  
      if (!formData.salary || isNaN(formData.salary)) {
        alert("Veuillez entrer un salaire valide !");
        return;
      }
  
  
      try {
        const response = await fetch(`http://localhost:5000/api/offers/${selectedOffer.id}`, {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            ...formData,
            salary: parseFloat(formData.salary),
          }),
        });
  
  
        if (!response.ok) throw new Error("Erreur lors de la modification de l'offre.");
  
  
        // ‚úÖ Mettre √† jour imm√©diatement les offres localement
        setOffers((prevOffers) =>
          prevOffers.map((offer) =>
            offer.id === selectedOffer.id ? { ...offer, ...formData } : offer
          )
        );
  
  
        closePopup(); // ‚úÖ Fermer le popup
      } catch (err) {
        console.error("Erreur lors de la mise √† jour de l'offre :", err.message);
      }
    };
     // üõ† Supprimer une offre
  const handleDeleteOffer = async (offerId) => {
    const token = localStorage.getItem("token");


    if (!window.confirm("Voulez-vous vraiment supprimer cette offre ?")) return;


    try {
      const response = await fetch(`http://localhost:5000/api/offers/${offerId}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });


      if (!response.ok) throw new Error("Erreur lors de la suppression de l'offre.");


      // ‚úÖ Supprimer imm√©diatement du state
      setOffers((prevOffers) => prevOffers.filter((offer) => offer.id !== offerId));
    } catch (err) {
      console.error("Erreur :", err.message);
    }
  };


  return (
    <section className="offers-section">
      <h2>{offers.length > 0 ? `${offers.length} offres trouv√©es` : "Aucune offre trouv√©e"}</h2>


      <div className="offers-list">
        {offers.map((offer) => (
          <div key={offer.id} className="offer-card">
            <h3>{offer.title}</h3>
            <p>Entreprise: {offer.company}</p>
            <p>Lieu: {offer.location}</p>
            <p>Salaire: {offer.salary} ‚Ç¨/mois</p>
            <p>Date de publication: {new Date(offer.created_at).toLocaleDateString()}</p>
            <button onClick={() => { setSelectedOffer(offer); setEditMode(false); }} className="view-offer-button">
              Voir l'offre
            </button>
            <button onClick={() => { setSelectedOffer(offer); setEditMode(true); setFormData(offer); }} className="modify-offer-button">
              Modifier
            </button>
            <button onClick={() => handleDeleteOffer(offer.id)} className="delete-offer-button">
              Supprimer
            </button>
          </div>
        ))}
      </div>


      {selectedOffer && (
        <div className="popup-overlay" onClick={closePopup}>
          <div className="popup" onClick={(e) => e.stopPropagation()}>
            <button className="close-popup" onClick={closePopup}>√ó</button>
            {editMode ? (
              <>
                <h2>Modifier l'offre</h2>
                <input type="text" placeholder="Titre" value={formData.title} onChange={(e) => setFormData({ ...formData, title: e.target.value })} />
                <input type="text" placeholder="Entreprise" value={formData.company} onChange={(e) => setFormData({ ...formData, company: e.target.value })} />
                <input type="text" placeholder="Lieu" value={formData.location} onChange={(e) => setFormData({ ...formData, location: e.target.value })} />
                <input type="number" placeholder="Salaire" value={formData.salary} onChange={(e) => setFormData({ ...formData, salary: e.target.value })} />
                <textarea placeholder="Description" value={formData.description} onChange={(e) => setFormData({ ...formData, description: e.target.value })} />
                <input type="text" placeholder="Lien" value={formData.lien} onChange={(e) => setFormData({ ...formData, lien: e.target.value })} />
                <button onClick={handleUpdateOffer} className="update-offer-button">Enregistrer</button>
              </>
            ) : ( <>
              <h2>{selectedOffer.title}</h2>
              <p><strong>Entreprise:</strong> {selectedOffer.company}</p>
              <p><strong>Lieu:</strong> {selectedOffer.location}</p>
              <p><strong>Salaire:</strong> {selectedOffer.salary} ‚Ç¨/mois</p>
              <p><strong>Date de publication:</strong> {new Date(selectedOffer.created_at).toLocaleDateString()}</p>
              <p><strong>Description:</strong> {selectedOffer.description}</p>
              <p><strong>Lien:</strong> <a href={selectedOffer.lien} target="_blank" rel="noopener noreferrer">{selectedOffer.lien}</a></p>
            </>
          )}
        </div>
      </div>
    )}
  </section>
);
};


export default Offers;
