import React, { useState, useEffect } from "react";
import "./Sidebar.css";
import avatar1 from "../../assets/avatar2.jpg";

function Sidebar() {
  const [userName, setUserName] = useState("Utilisateur");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [wishes, setWishes] = useState([]);
  const [experiences, setExperiences] = useState([]);

  const fetchUserInfo = async () => {
    try {
      const token = localStorage.getItem("token");
      const userId = localStorage.getItem("userId");

      const response = await fetch(
        `http://localhost:3002/api/profile/${userId}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error(
          "Erreur lors de la récupération des informations utilisateur."
        );
      }

      const data = await response.json();
      setUserName(`${data.user.firstName} ${data.user.lastName}`);
      setFirstName(data.user.firstName);
      setLastName(data.user.lastName);
      setWishes(data.wishes || []);
      setExperiences(data.experiences || []);
    } catch (err) {
      console.error("Erreur :", err.message);
    }
  };

  useEffect(() => {
    fetchUserInfo();
  }, []);

  return (
    <div className="sidebar">
      <img src={avatar1} alt="Profile" className="profile-picture" />
      <h2>
        {data.user.first_name} {profileData.user.name}
      </h2>
      {/* ✅ Section uniformisée avec la même classe pour Souhaits et Expérience */}
      <div className="info-section">
        <h3>Souhaits</h3>
        <ul>
          {wishes.length > 0 ? (
            wishes.map((wish, index) => <li key={index}>{wish.wish}</li>)
          ) : (
            <li>Aucun souhait ajouté</li>
          )}
        </ul>
      </div>

      <div className="info-section">
        <h3>Expérience</h3>
        <ul>
          {experiences.length > 0 ? (
            experiences.map((exp, index) => (
              <li key={index}>
                <strong>{exp.job}</strong> - {exp.duration} ({exp.rate} €/h)
              </li>
            ))
          ) : (
            <li>Aucune expérience ajoutée</li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default Sidebar;
