import React, { useState, useEffect } from "react";
import "./Sidebar.css";
import avatar1 from "../../assets/avatar2.jpg";

function Sidebar() {
  const [userName, setUserName] = useState("Utilisateur");
  const [wishes, setWishes] = useState([]); // Stocker les souhaits

  const fetchUserInfo = async () => {
    try {
      const token = localStorage.getItem("token");
      const userId = localStorage.getItem("userId");

      const response = await fetch(
        `http://localhost:3002/api/profile/${userId}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error(
          "Erreur lors de la récupération des informations utilisateur."
        );
      }

      const data = await response.json();
      setUserName(data.user.name);
      setWishes(data.wishes || []); // Mettre à jour les souhaits
    } catch (err) {
      console.error("Erreur :", err.message);
    }
  };

  useEffect(() => {
    fetchUserInfo();
  }, []);

  return (
    <div className="sidebar">
      <img src={avatar1} alt="Profile" className="profile-picture" />
      <h2>{userName}</h2>

      <div className="jobs-section">
        <h3>Souhaits</h3>
        <ul className="jobs-list">
          {wishes.length > 0 ? (
            wishes.map((wish, index) => <li key={index}>{wish.wish}</li>)
          ) : (
            <li>Aucun souhait ajouté</li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default Sidebar;
