document.addEventListener("mousemove", function (event) {
  const shape = document.querySelector(".fluid-shape");
  const x = (window.innerWidth / 2 - event.clientX) / 50;
  const y = (window.innerHeight / 2 - event.clientY) / 50;

  shape.style.transform = `rotate(10deg) translate(${x}px, ${y}px) scale(1.05)`;
});
