import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar/Sidebar";
import Tabs from "../components/Tabs/Tabs";
import Offers from "../components/Offers/Offers";
import "./DashboardPage.css";

function DashboardPage() {
  const [activeTab, setActiveTab] = useState("offresPostees");
  const [offresPostees, setOffresPostees] = useState([]);
  const [favoris, setFavoris] = useState([]);

  const userId = localStorage.getItem("userId"); // Récupère l'ID de l'utilisateur connecté

  // Récupérer les offres postées par l'utilisateur
  const fetchOffresPostees = async () => {
    try {
      const response = await fetch(
        `http://localhost:3002/api/offers/user/${userId}`
      );
      if (!response.ok)
        throw new Error("Erreur lors de la récupération des offres postées.");
      const data = await response.json();
      setOffresPostees(data);
    } catch (err) {
      console.error("Erreur :", err.message);
    }
  };

  // Récupérer les favoris de l'utilisateur
  const fetchFavoris = async () => {
    try {
      const response = await fetch(
        `http://localhost:3002/api/offers/favorites/${userId}`
      );
      if (!response.ok)
        throw new Error("Erreur lors de la récupération des favoris.");
      const data = await response.json();
      setFavoris(data);
    } catch (err) {
      console.error("Erreur :", err.message);
    }
  };

  useEffect(() => {
    fetchOffresPostees();
    fetchFavoris();
  }, []);

  return (
    <div className="dashboard-page">
      <Sidebar />

      <div className="main-content">
        <Tabs activeTab={activeTab} setActiveTab={setActiveTab} />

        <div className="tab-content">
          {activeTab === "offresPostees" && <Offers offers={offresPostees} />}
          {activeTab === "favoris" && <Offers offers={favoris} />}
        </div>
      </div>
    </div>
  );
}

export default DashboardPage;
